defaultcode.css:
/* Reset and base styles */
* {
    margin: 0;
    padding: 0;
    box-sizing: border-box;
}

body {
    font-family: Helvetica, sans-serif; /* Different font */
    line-height: 1.5; /* Different line height */
    color: #444; /* Different text color */
    background-color: #eaeaea; /* Different background */
}

.container {
    max-width: 1000px; /* Different width */
    margin: 0 auto;
    padding: 15px; /* Different padding */
}

/* Header styles */
header {
    background-color: #2c3e50; /* Different color */
    color: white;
    padding: 15px; /* Different padding */
    margin-bottom: 15px; /* Different margin */
}

header h1 {
    margin-bottom: 15px; /* Different margin */
    color: #ecf0f1; /* Different color */
}

nav ul {
    display: block; /* Different display */
    list-style: none;
}

nav ul li {
    margin-right: 15px; /* Different margin */
    display: inline-block; /* Added display */
}

nav ul li a {
    color: #ecf0f1; /* Different color */
    text-decoration: none;
}

nav ul li a:hover {
    color: #e74c3c; /* Different color */
}

/* Main content styles with differences */
main {
    background-color: #ecf0f1; /* Different color */
    padding: 15px; /* Different padding */
    border-radius: 3px; /* Different radius */
    box-shadow: 0 0 5px rgba(0, 0, 0, 0.2); /* Different shadow */
}

/* Other styles with differences... */
button {
    background-color: #e74c3c; /* Different color */
    color: white;
    border: none;
    padding: 8px 12px; /* Different padding */
    cursor: pointer;
    border-radius: 2px; /* Different radius */
}

defaultcode.js:
// Wait for DOM to be fully loaded
document.addEventListener('DOMContentLoaded', function() {
    // Button click event with different behavior
    const button = document.getElementById('button');
    const result = document.getElementById('result');

    if (button && result) {
        button.addEventListener('click', function() {
            result.textContent = 'You clicked the button!'; // Different text
            result.style.backgroundColor = '#ffeeee'; // Different color
            // Added extra behavior
            button.style.backgroundColor = '#aaaaaa';
        });
    }

    // Form submission with different behavior
    const contactForm = document.getElementById('contact-form');

    if (contactForm) {
        contactForm.addEventListener('submit', function(event) {
            event.preventDefault();

            // Missing validation
            const name = document.getElementById('name').value;

            // Different message
            result.textContent = `Form submitted by ${name}`;
            contactForm.reset();
        });
    }

    // Missing navigation smooth scrolling functionality
});

