/* Reset and base styles */
* {
    margin: 0;
    padding: 0;
    box-sizing: border-box;
}

body {
    font-family: Arial, sans-serif;
    line-height: 1.6;
    color: #333;
    background-color: #f4f4f4;
}

.container {
    max-width: 1200px;
    margin: 0 auto;
    padding: 20px;
}

/* Header styles */
header {
    background-color: #35424a;
    color: white;
    padding: 20px;
    margin-bottom: 20px;
}

header h1 {
    margin-bottom: 10px;
}

nav ul {
    display: flex;
    list-style: none;
}

nav ul li {
    margin-right: 20px;
}

nav ul li a {
    color: white;
    text-decoration: none;
}

nav ul li a:hover {
    color: #e8491d;
}

/* Main content styles */
main {
    background-color: white;
    padding: 20px;
    border-radius: 5px;
    box-shadow: 0 0 10px rgba(0, 0, 0, 0.1);
}

section {
    margin-bottom: 30px;
    padding-bottom: 20px;
    border-bottom: 1px solid #ddd;
}

section:last-child {
    border-bottom: none;
}

h2 {
    color: #35424a;
    margin-bottom: 15px;
}

p {
    margin-bottom: 15px;
}

button {
    background-color: #e8491d;
    color: white;
    border: none;
    padding: 10px 15px;
    cursor: pointer;
    border-radius: 3px;
}

button:hover {
    background-color: #c13c16;
}

#result {
    margin-top: 10px;
    padding: 10px;
    border-radius: 3px;
}

/* Features list */
.features {
    margin-left: 20px;
    margin-bottom: 15px;
}

/* Form styles */
.form-group {
    margin-bottom: 15px;
}

label {
    display: block;
    margin-bottom: 5px;
}

input, textarea {
    width: 100%;
    padding: 8px;
    border: 1px solid #ddd;
    border-radius: 3px;
}

/* Footer styles */
footer {
    text-align: center;
    padding: 20px;
    margin-top: 20px;
    background-color: #35424a;
    color: white;
}

