// Wait for DOM to be fully loaded
document.addEventListener('DOMContentLoaded', function() {
    // Button click event
    const button = document.getElementById('button');
    const result = document.getElementById('result');

    if (button && result) {
        button.addEventListener('click', function() {
            result.textContent = 'Button clicked!';
            result.style.backgroundColor = '#e8f4f8';
        });
    }

    // Form submission
    const contactForm = document.getElementById('contact-form');

    if (contactForm) {
        contactForm.addEventListener('submit', function(event) {
            event.preventDefault();

            // Get form values
            const name = document.getElementById('name').value;
            const email = document.getElementById('email').value;
            const message = document.getElementById('message').value;

            // Simple validation
            if (name && email && message) {
                // In a real app, you would send this data to a server
                result.textContent = `Thank you, ${name}! Your message has been sent.`;
                result.style.backgroundColor = '#e8f8e8';
                contactForm.reset();
            } else {
                result.textContent = 'Please fill out all fields.';
                result.style.backgroundColor = '#f8e8e8';
            }
        });
    }

    // Navigation smooth scrolling
    const navLinks = document.querySelectorAll('nav a');

    navLinks.forEach(link => {
        link.addEventListener('click', function(event) {
            event.preventDefault();

            const targetId = this.getAttribute('href').substring(1);
            const targetSection = document.getElementById(targetId);

            if (targetSection) {
                window.scrollTo({
                    top: targetSection.offsetTop - 70,
                    behavior: 'smooth'
                });
            }
        });
    });
});

